/* eslint-disable react-hooks/exhaustive-deps */
import React, {useState, useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import jwt_decode from 'jwt-decode';
import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
  FlatList,
} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome5';
import SimpleIcon from 'react-native-vector-icons/SimpleLineIcons';
import LoadingModal from '../components/LoadingModal';
import account from '../assets/account.jpg';
import {API_URL} from '@env';

import {Thumbnail} from 'native-base';

// import action
import contactAction from '../redux/actions/contact';

const Friend = (props) => {
  const [hide, setHide] = useState(false);
  const [search, setSearch] = useState('');
  const [totalFriend, setTotalFriend] = useState(null);

  const navigateTo = (id) =>
    props.navigation.navigate('ChatDetail', {
      id: Number(id),
    });

  const dispatch = useDispatch();
  const token = useSelector((state) => state.auth.token);

  const {id: userId} = jwt_decode(token);

  const searchFriend = async () => {
    try {
      await dispatch(contactAction.listFriend(token, search)).catch((e) => {
        console.log(e.message);
      });
    } catch (err) {
      console.log(err.message);
    }
  };

  useEffect(() => {
    dispatch(contactAction.listFriend(token));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const [data, setData] = useState([]);
  const contactState = useSelector((state) => state.contact);
  const {listContact} = contactState;

  useEffect(() => {
    if (listContact) {
      setTotalFriend(listContact.results.length);
      setData(listContact.results);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [listContact]);

  const Item = ({item, onPress, style}) =>
    item.Friend.id !== userId ? (
      <TouchableOpacity
        style={styles.friendContact}
        onPress={() => navigateTo(item.Friend.id)}>
        <Thumbnail
          small
          source={
            item.Friend.profile_image
              ? {uri: API_URL + item.Friend.profile_image}
              : account
          }
        />
        <Text style={styles.friendContact_Account_name}>
          {item.Friend.username}
        </Text>
      </TouchableOpacity>
    ) : (
      <TouchableOpacity
        style={styles.friendContact}
        onPress={() => navigateTo(item.Self.id)}>
        <Thumbnail
          small
          source={
            item.Self.profile_image
              ? {uri: API_URL + item.Self.profile_image}
              : account
          }
        />
        <Text style={styles.friendContact_Account_name}>
          {item.Self.username}
        </Text>
      </TouchableOpacity>
    );

  return (
    <View style={styles.parent}>
      <LoadingModal />
      <View
        style={[
          styles.searchBar,
          search.length > 0 && {borderColor: '#00B900', borderBottomWidth: 1},
        ]}>
        <TouchableOpacity onPress={searchFriend}>
          <Icon name="search" size={18} color="grey" />
        </TouchableOpacity>
        <TextInput
          placeholder="Cari dengan Nama"
          onSubmitEditing={searchFriend}
          style={styles.searchInput}
          onChangeText={(text) => setSearch(text)}
          value={search}
        />
        {search.length > 0 && (
          <TouchableOpacity
            style={styles.btnClear}
            onPress={() => {
              setSearch('');
              dispatch(contactAction.listFriend(token));
            }}>
            <Icon name="times" size={20} color="#a5acaf" />
          </TouchableOpacity>
        )}
      </View>
      <TouchableOpacity
        onPress={() => setHide(!hide)}
        style={{flexDirection: 'row', justifyContent: 'space-between'}}>
        <Text>Teman {totalFriend}</Text>
        <SimpleIcon name={!hide ? 'arrow-up' : 'arrow-down'} size={12} />
      </TouchableOpacity>
      {!hide && (
        <View style={{flex: 1}}>
          <View style={styles.verified}>
            <Thumbnail
              small
              source={{
                uri:
                  'data:image/jpeg;base64,',
              }}
            />
            <Text style={styles.verified_Account_name}>Akun Resmi</Text>
            <Icon name="chevron-right" size={13} color="rgba(0,0,0, 0.5)" />
          </View>
          <FlatList
            data={data}
            renderItem={Item}
            keyExtractor={(item) => item.id.toString()}
            showsVerticalScrollIndicator={false}
          />
        </View>
      )}
    </View>
  );
};

export default Friend;

const styles = StyleSheet.create({
  parent: {
    padding: 15,
    paddingVertical: 0,
    backgroundColor: 'white',
    flex: 1,
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    borderBottomWidth: 0.5,
    borderColor: 'grey',
    paddingVertical: 0,
    marginBottom: 5,
  },
  searchInput: {
    flex: 1,
    height: 40,
    fontStyle: 'italic',
    paddingLeft: 8,
  },
  btnClear: {
    position: 'absolute',
    top: 12,
    right: 0,
  },
  verified: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 13,
  },
  verified_Account_name: {
    marginLeft: 13,
    marginRight: 10,
    fontSize: 14,
  },
  friendContact: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 13,
  },
  friendContact_Account_name: {
    marginLeft: 13,
    marginRight: 10,
    fontSize: 14,
  },
});
